var model = {
    users: [{}][{name: 'zfpx1', age: 1, __v: 0},
    {name: 'zfpx2', age: 2, __v: 0},
    {name: 'zfpx3', age: 3, __v: 0},
    {name: 'zfpx4', age: 4, __v: 0},
    {name: 'zfpx5', age: 5, __v: 0},
    {name: 'zfpx6', age: 6, __v: 0},
    {name: 'zfpx7', age: 7, __v: 0},
    {name: 'zfpx8', age: 8, __v: 0},
    {name: 'zfpx9', age: 9, __v: 0},
    {name: 'zfpx10', age: 10, __v: 0}],
    sort(sortObj){//{age:1}
        var attr=Object.keys(sortObj)[0];//取到age
        this._sort=function (a,b) {
            return (a.attr-b.attr)*sortObj[attr];
        };
        return this;
    },
    skip(){
        this._skip=skip;
        return this;
    },
    limit(){
        this._skip=limit;
        return this;
    },
    exec(cb){
        var result=this.users.sort(this._sort)
            .slice(this._skip,this._skip+this._limit);
        cb(null,result);
    }
};

var pageNum=3;
var pageSize=3;
